mapper.select:
SELECT * FROM ( SELECT t1.*, ROWNUM RN FROM (select a.ownerid,d.deptname,a.linkid,e.linkcode,e.linkname,a.goodid,b.goods,b.name,b.spec,b.msunitno,b.packnum,b.producer, b.warebrand goodsbrand, a.buyerid,c.empname as buyername,a.warebrand,a.createdate,a.createuser,c1.empcode createempcode,c1.empname createempname from user_hn_linkbuyer a, pub_waredict b, pub_emp c, pub_dept d, pub_emp c1, scm_buyercst_linkman e where a.goodid = b.goodid and a.buyerid = c.empid and a.ownerid = d.deptid and a.createuser = c1.empid and a.linkid = e.linkid and a.OWNERID=${OWNERID}
and ( b.name like '%${name}%' or a.goodid like '%${goodid}%')
) t1 WHERE ROWNUM <= ${size} ) WHERE RN > ${startpage}

mapper.update:
UPDATE user_hn_linkbuyer
SET buyerid=${BUYERID},warebrand='${WAREBRAND}'
WHERE ownerid=${OWNERID} and goodid=${GOODID} and linkid=${LINKID} and buyerid=${BUYERIDS}
